SELECT ARRAY_AGG(DISTINCT x IGNORE NULLS) AS array_agg
FROM t;

SELECT ARRAY_AGG(x IGNORE NULLS) AS array_agg
FROM t;

SELECT ARRAY_AGG(x RESPECT NULLS) AS array_agg
FROM t;

SELECT ARRAY_AGG(DISTINCT page LIMIT 50)
FROM t;

SELECT ARRAY_AGG(cors_origin_value IGNORE NULLS LIMIT 5)
FROM t;

SELECT ARRAY_AGG(x ORDER BY y LIMIT 10) AS array_agg
FROM t;

SELECT ARRAY_AGG(DISTINCT x ORDER BY LENGTH(x) DESC LIMIT 5) AS array_agg
FROM t;

SELECT ARRAY_AGG(x IGNORE NULLS ORDER BY y, z LIMIT 3) AS array_agg
FROM t;

SELECT ARRAY_AGG(x ORDER BY y LIMIT 10) OVER (PARTITION BY z) AS array_agg
FROM t;

SELECT ARRAY_AGG(x) OVER (PARTITION BY z) AS array_agg
FROM t;
