-- RESUME / SUSPEND
ALTER ALERT my_alert RESUME;

ALTER ALERT my_alert SUSPEND;

-- SET properties
ALTER ALERT my_alert SET WAREHOUSE = my_new_warehouse;

ALTER ALERT my_alert SET SCHEDULE = '10 MINUTE';

ALTER ALERT my_alert SET COMMENT = 'updated alert';

-- UNSET
ALTER ALERT my_alert UNSET WAREHOUSE;

ALTER ALERT my_alert UNSET COMMENT;

-- MODIFY CONDITION
ALTER ALERT my_alert MODIFY CONDITION EXISTS (SELECT 1 FROM errors WHERE severity = 'HIGH');

-- MODIFY ACTION
ALTER ALERT my_alert MODIFY ACTION INSERT INTO alert_log VALUES (CURRENT_TIMESTAMP());
