-- Basic aggregation policy
CREATE AGGREGATION POLICY my_agg_policy
  AS () RETURNS AGGREGATION_CONSTRAINT ->
    AGGREGATION_CONSTRAINT(MIN_GROUP_SIZE => 5);

-- OR REPLACE
CREATE OR REPLACE AGGREGATION POLICY my_agg_policy
  AS () RETURNS AGGREGATION_CONSTRAINT ->
    NO_AGGREGATION_CONSTRAINT()
  COMMENT = 'no restrictions';

-- IF NOT EXISTS
CREATE AGGREGATION POLICY IF NOT EXISTS my_agg_policy
  AS () RETURNS AGGREGATION_CONSTRAINT ->
    AGGREGATION_CONSTRAINT(MIN_GROUP_SIZE => 1000);
