CREATE AUTHENTICATION POLICY my_policy;

CREATE AUTHENTICATION POLICY IF NOT EXISTS my_authentication_policy
   AUTHENTICATION_METHODS = ( 'MY METHOD'  )
    CLIENT_TYPES = ( 'MY CLIENT' )
   COMMENT = 'My Comment' ;

CREATE AUTHENTICATION POLICY IF NOT EXISTS my_authentication_policy
   AUTHENTICATION_METHODS = ( 'METHOD1', 'METHOD2', 'METHOD3'  )
    CLIENT_TYPES = ( 'CLIENT1', 'CLIENT2', 'CLIENT3' )
   COMMENT = 'My Comment' ;

CREATE AUTHENTICATION POLICY my_policy
    MFA_AUTHENTICATION_METHODS = ('METHOD1')
    MFA_ENROLLMENT = REQUIRED
    SECURITY_INTEGRATIONS = ('INTEGRATION1');

CREATE OR REPLACE AUTHENTICATION POLICY my_policy
    MFA_AUTHENTICATION_METHODS = ( 'METHOD1', 'METHOD2', 'METHOD3'  )
    MFA_ENROLLMENT = REQUIRED
    SECURITY_INTEGRATIONS = ('INTEGRATION1', 'INTEGRATION2', 'INTEGRATION3');
